import React from "react";
import { useNavigate } from "react-router-dom";

//components
import UserSelectBtn from "../Components/UserSelectBtn.jsx";

//css
import "./Home.css";
import "../Components/UserSelectBtn.css";

function Home() {
  const navigate = useNavigate();

  function handleCreateGame() {
    navigate("/host");
  }
  function handleJoinLobby() {
    navigate("/join");
  }

  return (
    <div>
      <h1 id="logo">
        TRIVIA<span id="name">SMASH</span>
      </h1>
      <div id="buttonContainer">
        <UserSelectBtn
          onclickFct={handleCreateGame}
          id="gameButton1"
          title="Create Game"
        />
        <UserSelectBtn
          onclickFct={handleJoinLobby}
          id="gameButton2"
          title="Join Lobby"
        />
      </div>
      <h3 id="disc"></h3>
    </div>
  );
}

export default Home;
