import React from "react";
import "./Host.css";

import PlayerCard from "../Components/PlayerCard";
//category Select
//User- Select what categories you want your rivals to be quizzed on
//DEV - Map the categories from the API to be rendered on the screen for user to see

//Heading = Header Game Code
//Players Joined
//Settings
//Start Game

function Host() {
  function getTrivia() {
    fetch("https://the-trivia-api.com/v2/questions")
      .then((response) => response.json())
      .then((data) => {
        let categoryArray;
        data.map((item) => categoryArray.push(item.category));
        console.log(categoryArray);
        // data.map((data) => {
        //   if (data.category === "music") {
        //     console.log(data);
        //   }
        // });
      })
      .catch((err) => console.error(err));
  }

  return (
    <main id="host">
      <h1 id="pageHeader">Create Game</h1>
      {/* H1 GAME CODE  generate a single use code*/}
      <h1 id="gameCode">Lobby Code: 696969</h1>
      <button onClick={getTrivia}>GET TRIVIA</button>

      <div id="playerCardContainer">
        {/* titles/colors will be grabbed from backend */}
        <PlayerCard id="username" username="NIG" color="coral" />
        <PlayerCard id="username2" username="Player 2" />
        <PlayerCard id="username3" username="Player 3" />
        <PlayerCard id="username4" username="Player 4" />
      </div>
      <div id="createGameSettings">
        {/* <button className="gamemodeBtn">Classic</button> */}
        {/* <button className="gamemodeBtn">Fast Mode</button> */}
        <div>
          <h3>Game Parameters</h3>
          <div id="gameParamsContainer">
            <button>P1</button>
            <button>P2</button>
            <button>P3</button>
            <button>P4</button>
            <button>P5</button>
            <button>P6</button>
            <button>P7</button>
            <button>P8</button>
          </div>
        </div>
        <div id="gameCategoriesContainer">
          <h3>Categories</h3>
          <div>
            {/* {fetch("/endpoint")
              .then("parse json")
              .then(
                "array.map(cat) => return <CategorySelect category={cat.category} categoryName={cat.category}>"
              )} */}
          </div>
        </div>
      </div>

      <button id="startGameBtn">Start Game</button>
    </main>
  );
}

export default Host;
